SELECT cd.dept_name AS deptName, cd.dept_code AS deptCode, cd.parent_code AS parentCode, cd.`status` AS status, cd.creator AS creator, cd.create_time AS createTime FROM client_dept cd WHERE cd.client_user_company_id = #{clientUserCompanyId,jdbcType=BIGINT} AND cd.parent_code = '0' AND (cd.status = 1 OR cd.status = 2)
AND cd.dept_name LIKE CONCAT('%',#{deptNameCode},'%') OR cd.dept_code = #{deptNameCode}
ORDER BY cd.create_time DESC

