mapper.select:
SELECT id as deptId, merchant_id as merchantId, dept_code as deptCode, parent_code as parentCode, dept_name as deptName, status as status FROM merchant_dept WHERE merchant_id = #{merchantId,jdbcType=BIGINT} AND dept_name = #{deptName,jdbcType=VARCHAR}
AND parent_code = #{parentCode,jdbcType=VARCHAR}
AND status != 0

SELECT count(1) FROM 
merchant_dept WHERE parent_code = 0 and dept_name = #{deptName,jdbcType=VARCHAR} 
AND merchant_id = #{merchantId,jdbcType=BIGINT} AND status != 0

