mapper.insert:
insert into merchant_role (id, merchant_id, role_code, role_name, 
  remark, status, create_time, 
  creator, last_modify_time, modifier
  )
values (#{id,jdbcType=BIGINT}, #{merchantId,jdbcType=BIGINT}, #{roleCode,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, 
  #{remark,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{creator,jdbcType=VARCHAR}, #{lastModifyTime,jdbcType=TIMESTAMP}, #{modifier,jdbcType=VARCHAR}
  )

mapper.select:
select 

from merchant_role
where role_code = #{roleCode}
and merchant_id = #{merchantId}
and status = 1

select  
from merchant_role
where role_name = #{roleName}
and status = 1
and merchant_id = #{merchantId}

    and role_code <> #{roleCode}

SELECT r.role_code AS roleCode, r.role_name AS roleName, r.remark AS remark, u.merchant_user_name AS creator, r.create_time AS createTime FROM merchant_role r JOIN merchant_user u ON u.merchant_user_code = r.creator WHERE u.status != 0 and r.status != 0 and u.merchant_code = #{merchantCode}
and ( r.role_code = #{searchInput} OR r.role_name = #{searchInput} )
order by r.create_time desc
