mapper.select:
select
,
from merchant_goods_module where module_status != 0 and module_code = #{moduleCode}

  	SELECT
		count(1)
	FROM 
		merchant_goods_module mgm
	
		mgm.module_status != 0
		
			AND mgm.merchant_id = #{merchantId}
		
		
			AND mgm.module_name = #{moduleName}
		
		
			AND mgm.module_code != #{moduleCode}
