mapper.select:
select merchant_code merchantCode, registered_person registeredPerson, registered_person createName, contact_phone contactPhone, company_name companyName, status status, create_time createTime from merchant
and merchant_code = #{param} or company_name = #{param} or registered_person = #{param}
ORDER BY create_time DESC

select m.merchant_user_code merchantUserCode, m.merchant_user_name merchantUserName, m.phone_number phoneNumber, m.post_name postName, a.dept_name deptName, m.status status, m.create_time createTime from merchant_user m,platform_dept a where m.dept_id = a.id
and m.merchant_user_name = #{param} or m.phone_number = #{param} or m.post_name = #{param} or a.dept_name = #{param}
ORDER BY m.create_time DESC
