mapper.select:
  	SELECT
		count(1)
	FROM 
		platform_combination pc
	
		pc.status != 0
		AND pc.combination_name = #{combinationName}
		AND pc.case_id = #{caseId}
		
			AND pc.combination_code != #{combinationCode}

  	SELECT
		count(1)
	FROM 
		platform_combination pc
	
		pc.status != 0
		AND pc.combination_name = #{combinationName}
		AND pc.case_id = #{caseId}
		
			AND pc.combination_code != #{combinationCode}

SELECT count(1) FROM platform_combination_module pcm JOIN platform_combination pc ON pc.id = pcm.combination_id JOIN platform_case c ON c.id = pc.case_id WHERE pc.`status` != 0 AND c.case_status != 0 and c.id = #{caseId}
AND pc.combination_code != #{combinationCode}
AND pcm.module_id in
#{moduleId}
