mapper.select:
SELECT special_performance_code as specialPerformanceCode, special_performance_name as specialPerformanceName, special_performance_main_pic_url as specialPerformanceMainPicUrl, special_performance_main_pic_name as specialPerformanceMainPicName, special_performance_bg_pic_url as specialPerformanceBgPicUrl, special_performance_bg_pic_name as specialPerformanceBgPicName, special_performance_link as specialPerformanceLink, module_number as moduleNumber, product_number as productNumber, interview_number as interviewNumber, status as status, create_time as createTime FROM platform_special_performance
and special_performance_name like CONCAT('%',#{specialPerformanceName},'%') and status = #{status}
ORDER BY create_time DESC
