mapper.insert:
insert into platform_dept (id, dept_code, dept_name, 
  description, status, create_time, 
  creator, last_modify_time, modifier
  )
values (#{id,jdbcType=BIGINT}, #{deptCode,jdbcType=VARCHAR}, #{deptName,jdbcType=VARCHAR}, 
  #{description,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{creator,jdbcType=VARCHAR}, #{lastModifyTime,jdbcType=TIMESTAMP}, #{modifier,jdbcType=VARCHAR}
  )

mapper.select:
SELECT id as id, dept_code as deptCode, dept_name as deptName, description as description, status as status, create_time as createTime, creator as creator, last_modify_time as lastModifyTime, modifier as modifier FROM platform_dept
AND dept_name LIKE CONCAT('%',#{deptName},'%') AND description LIKE CONCAT('%',#{description},'%') AND status = #{status}
ORDER BY create_time DESC
