SELECT pgpp.params_term_id AS paramsTermId, pgpp.params_type AS paramsType, pgpp.params_name AS paramsName, group_concat(DISTINCT pgpp.params_value) AS paramValues FROM merchant_goods_product_params pgpp JOIN merchant_goods_product mgp on mgp.id = pgpp.product_id and mgp.product_status = 1 JOIN platform_combination_module pcm ON pcm.module_id = mgp.module_id JOIN platform_combination pc ON pc.id = pcm.combination_id JOIN platform_case cs ON pc.case_id = cs.id WHERE cs.case_status = 1 AND pc.status = 1 AND cs.case_code = #{caseCode}
AND pgpp.params_term_id not in
#{inputParam.paramsTermId}
AND pgpp.params_term_id not in
#{selectParam.paramsTermId}
AND pgpp.product_id IN ( SELECT DISTINCT pgpp.product_id FROM merchant_goods_product_params pgpp JOIN merchant_goods_product mgp on mgp.id = pgpp.product_id and mgp.product_status = 1
AND pgpp.product_id IN ( SELECT distinct pgpp.product_id FROM merchant_goods_product_params pgpp WHERE pgpp.params_type = 2 AND pgpp.params_term_id = #{inputParam.paramsTermId} AND pgpp.params_value = #{inputParam.paramsValue} )
AND pgpp.product_id IN ( SELECT distinct pgpp.product_id FROM merchant_goods_product_params pgpp WHERE pgpp.params_type = 1 AND pgpp.params_term_id = #{selectParam.paramsTermId} AND pgpp.params_value = #{selectParam.paramsValue} )
) GROUP BY pgpp.params_term_id

